mapper.select:
select url.url from (

    select t.id,t.name,t.icon,t.path,t.component,t.keep_alive,t.auth,t.sort,t.pid FROM (
    select  m.* from ua_role_menu as rm, ua_menu as m where rm.role_id = #{role.roleId}
    and rm.menu_id = m.id and rm.checked = 1 group by m.id) as t

) as m, ua_menu_url as menuUrl, ua_url as url where m.id = menuUrl.menu_id
and menuUrl.checked = 1 and url.id = menuUrl.url_id

select url.url from (select sm.* from (

    select t.id,t.name,t.icon,t.path,t.component,t.keep_alive,t.auth,t.sort,t.pid FROM (
    select  m.* from ua_role_menu as rm, ua_menu as m where rm.role_id = #{orgRole.roleId}
    and rm.menu_id = m.id and rm.checked = 1 group by m.id) as t)

) as pm join (

    select t.id,t.name,t.icon,t.path,t.component,t.keep_alive,t.auth,t.sort,t.pid FROM (
    select  m.* from ua_role_menu as rm, ua_menu as m where rm.role_id = #{userRole.roleId}
    and rm.menu_id = m.id and rm.checked = 1 group by m.id) as t

) as sm on sm.id = pm.id

select url.url from (

    select t.id,t.name,t.icon,t.path,t.component,t.keep_alive,t.auth,t.sort,t.pid FROM (
    select  m.* from ua_role_menu as rm, ua_menu as m where rm.role_id = #{role.roleId}
    and rm.menu_id = m.id and rm.checked = 1 group by m.id) as t

) as m,ua_menu_url as menuUrl,ua_url as url where m.id = menuUrl.menu_id and menuUrl.checked = 1
and url.id = menuUrl.url_id
